INSERT INTO `wp_options` VALUES (516, '_transient_eps_plugin_falcon', 'a:25:{s:4:\"name\";s:51:\"Falcon &#8211; WordPress Optimizations &amp; Tweaks\";s:4:\"slug\";s:6:\"falcon\";s:7:\"version\";s:5:\"2.8.3\";s:6:\"author\";s:43:\"<a href=\"https://elightup.com\">eLightUp</a>\";s:14:\"author_profile\";s:38:\"https://profiles.wordpress.org/rilwis/\";s:8:\"requires\";s:3:\"5.9\";s:6:\"tested\";s:5:\"6.5.5\";s:12:\"requires_php\";s:3:\"7.4\";s:16:\"requires_plugins\";a:0:{}s:6:\"rating\";i:100;s:7:\"ratings\";a:5:{i:5;i:9;i:4;i:0;i:3;i:0;i:2;i:0;i:1;i:0;}s:11:\"num_ratings\";i:9;s:15:\"support_threads\";i:0;s:24:\"support_threads_resolved\";i:0;s:15:\"active_installs\";i:2000;s:10:\"downloaded\";i:39460;s:12:\"last_updated\";s:21:\"2024-05-17 7:32am GMT\";s:5:\"added\";s:10:\"2015-12-29\";s:8:\"homepage\";s:20:\"https://elightup.com\";s:17:\"short_description\";s:78:\"A lightweight WordPress optimization and tweak plugin for a better performance\";s:11:\"description\";s:4138:\"<p><strong>Falcon</strong> is lightweight WordPress plugin that provide a list of optimizations and tweaks that help you improve the performance and user experience for your WordPress sites.</p>\n<h3>FEATURES &amp; MODULES</h3>\n<p>Falcon offers a comprehensive list of 49 FREE modules (and growing) for you to tweak and optimize your WordPress websites. These options are divided into the following categories:</p>\n<h3>General</h3>\n<ul>\n<li><a href=\"https://metabox.io/disable-gutenberg-without-using-plugins/\" rel=\"nofollow ugc\">Disable Gutenberg</a> (the block editor)</li>\n<li>Disable heartbeat</li>\n<li>Disable embeds, e.g. prevent others from embedding your site and vise-versa</li>\n<li>Disable comments</li>\n<li>Remove website field from comment form</li>\n<li>Disable revisions</li>\n<li>Disable self pings</li>\n<li>Disable privacy tools</li>\n<li>Disable cron</li>\n<li>Disable auto updates</li>\n<li>Block external requests</li>\n<li>Search only posts</li>\n<li>Disable replacing text with formatted entities like smart quotes, dashes, ellipses, etc.</li>\n</ul>\n<h3>Header cleanup</h3>\n<ul>\n<li>Remove feed links</li>\n<li>Remove RSD link</li>\n<li>Remove wlwmanifest link</li>\n<li>Remove adjacent posts links</li>\n<li>Remove WordPress version number</li>\n<li>Remove shortlink</li>\n<li>Remove REST API link</li>\n</ul>\n<h3>Media</h3>\n<ul>\n<li><strong>Asynchronous load CSS</strong> to avoid blocking load of CSS files</li>\n<li>Remove query string for JavaScript and CSS files</li>\n<li>Remove jQuery Migrate</li>\n<li>Set scheme-less URLs for JavaScript and CSS files, e.g. remove <code>http:</code> and <code>https:</code> from URLs</li>\n<li>Remove styles for recent comments widget</li>\n<li>Cleanup nav menu item ID &amp; classes</li>\n<li>Disable emojis</li>\n<li>Disable scaling down big images</li>\n<li>Disable automatic image rotation based on EXIF data</li>\n<li>Disable thumbnail generation</li>\n<li>Enable maintenance mode</li>\n</ul>\n<h3>Email</h3>\n<ul>\n<li>Remove admin email confirmation</li>\n<li>Disable auto update email notification</li>\n<li>Disable admin email notification when a new user is registered</li>\n<li>Disable admin email notification when users reset passwords</li>\n<li><a href=\"https://deluxeblogtips.com/change-wordpress-default-email/\" rel=\"nofollow ugc\">Change default WordPress from name and email</a></li>\n<li>SMTP configuration</li>\n</ul>\n<h3>Admin</h3>\n<ul>\n<li>Show site icon on login page</li>\n<li>Remove update nags</li>\n<li>Remove footer text</li>\n<li>Remove default dashboard widgets</li>\n<li>Remove WordPress logo in the admin bar</li>\n<li>Remove application passwords</li>\n</ul>\n<h3>Security</h3>\n<ul>\n<li>Disable REST API for unauthenticated requests</li>\n<li><a href=\"https://deluxeblogtips.com/disable-xml-rpc-wordpress/\" rel=\"nofollow ugc\">Disable XML-RPC</a></li>\n<li>Restrict upload file types</li>\n<li>Disable detailed login errors</li>\n<li>Block AI bots from crawling/stealing your content, which also affect the performance</li>\n<li>Force login to view the website</li>\n</ul>\n<h3>You might also like</h3>\n<p>If you like this plugin, you might also like our other WordPress products:</p>\n<ul>\n<li><a href=\"https://metabox.io\" rel=\"nofollow ugc\">Meta Box</a> &#8211; A powerful WordPress plugin for creating custom post types and custom fields.</li>\n<li><a href=\"https://wpslimseo.com\" rel=\"nofollow ugc\">Slim SEO</a> &#8211; A fast, lightweight and full-featured SEO plugin for WordPress with minimal configuration.</li>\n<li><a href=\"https://wpslimseo.com/products/slim-seo-schema/\" rel=\"nofollow ugc\">Slim SEO Schema</a> &#8211; An advanced, powerful and flexible plugin to add schemas to WordPress.</li>\n<li><a href=\"https://wpslimseo.com/products/slim-seo-link-manager/\" rel=\"nofollow ugc\">Slim SEO Link Manager</a> &#8211; Build internal link easier in WordPress with real-time reports.</li>\n<li><a href=\"https://gretathemes.com\" rel=\"nofollow ugc\">GretaThemes</a> &#8211; Free and premium WordPress themes that clean, simple and just work.</li>\n<li><a href=\"https://wpautolistings.com\" rel=\"nofollow ugc\">Auto Listings</a> &#8211; A car sale and dealership plugin for WordPress.</li>\n</ul>\n\";s:13:\"download_link\";s:55:\"https://downloads.wordpress.org/plugin/falcon.2.8.3.zip\";s:4:\"tags\";a:5:{s:5:\"admin\";s:5:\"admin\";s:8:\"optimize\";s:8:\"Optimize\";s:11:\"performance\";s:11:\"performance\";s:5:\"speed\";s:5:\"speed\";s:6:\"tweaks\";s:6:\"tweaks\";}s:11:\"donate_link\";s:0:\"\";s:5:\"icons\";a:2:{s:2:\"1x\";s:51:\"https://ps.w.org/falcon/assets/icon.svg?rev=3052547\";s:3:\"svg\";s:51:\"https://ps.w.org/falcon/assets/icon.svg?rev=3052547\";}}', 'off');
