INSERT INTO `wp_postmeta` VALUES (398, 95, '_mail_2', 'a:9:{s:6:\"active\";b:0;s:7:\"subject\";s:30:\"[_site_title] \"[your-subject]\"\";s:6:\"sender\";s:42:\"[_site_title] <thienbui07081999@gmail.com>\";s:4:\"body\";s:315:\"Nội dung thông điệp:\n[your-message]\n\n-- \nEmail này là biên nhận cho việc bạn đã gửi biểu mẫu liên hệ trên trang web của chúng tôi ([_site_title] [_site_url]) trong đó địa chỉ email của bạn đã được sử dụng. Nếu không phải bạn, vui lòng bỏ qua email này.\";s:9:\"recipient\";s:12:\"[your-email]\";s:18:\"additional_headers\";s:29:\"Reply-To: [_site_admin_email]\";s:11:\"attachments\";s:0:\"\";s:8:\"use_html\";i:0;s:13:\"exclude_blank\";i:0;}');
INSERT INTO `wp_postmeta` VALUES (399, 95, '_messages', 'a:12:{s:12:\"mail_sent_ok\";s:53:\"Xin cảm ơn, form đã được gửi thành công.\";s:12:\"mail_sent_ng\";s:118:\"Có lỗi xảy ra trong quá trình gửi. Xin vui lòng thử lại hoặc liên hệ người quản trị website.\";s:16:\"validation_error\";s:86:\"Có một hoặc nhiều mục nhập có lỗi. Vui lòng kiểm tra và thử lại.\";s:4:\"spam\";s:118:\"Có lỗi xảy ra trong quá trình gửi. Xin vui lòng thử lại hoặc liên hệ người quản trị website.\";s:12:\"accept_terms\";s:67:\"Bạn phải chấp nhận điều khoản trước khi gửi form.\";s:16:\"invalid_required\";s:48:\"Vui lòng nhập dữ liệu cho trường này.\";s:16:\"invalid_too_long\";s:54:\"Trường dữ liệu này có mục nhập quá dài.\";s:17:\"invalid_too_short\";s:51:\"Trường dữ liệu có mục nhập quá ngắn.\";s:13:\"upload_failed\";s:36:\"Tải file lên không thành công.\";s:24:\"upload_file_type_invalid\";s:69:\"Bạn không được phép tải lên file theo định dạng này.\";s:21:\"upload_file_too_large\";s:50:\"Dung lượng file được tải lên quá lớn.\";s:23:\"upload_failed_php_error\";s:36:\"Tải file lên không thành công.\";}');
