INSERT INTO `wp_posts` VALUES (99, 1, '2024-09-20 17:49:57', '2024-09-20 10:49:57', '<p>Chào mừng các bạn đã dành thời gian ghé thăm TopListCafe.</p><p>Những điều khoản và đề mục trong phần này có giá trị và hiệu lực áp dụng cho toàn bộ nội dung và tài sản trí tuệ trực thuộc website <a href=\"https://toplistcafe.com/\" data-type=\"URL\" data-id=\"https://toplistcafe.com/\">toplistcafe.com</a>. Truy cập toplistcafe.com cũng đồng nghĩa với việc bạn chấp nhận đồng ý với những điều khoản này.</p>', 'Chính sách và Điều khoản', '', 'publish', 'closed', 'closed', '', 'chinh-sach-va-dieu-khoan', '', '', '2024-09-20 17:49:57', '2024-09-20 10:49:57', '', 0, 'http://localhost/toplistcafe-wp/?page_id=99', 0, 'page', '', 0);
